package uk.co.crawj.week1;

import static org.junit.jupiter.api.Assertions.assertEquals;

import java.util.stream.Stream;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import uk.co.crawj.utility.StarLevel;

class Dec6Test
{
    static Stream<Arguments> testInputs()
    {
        return Stream.of(
                Arguments.of( "mjqjpqmgbljsphdztnvjfqwrcgsmlb", 7, 19 ),
                Arguments.of( "bvwbjplbgvbhsrlpgdmjqwftvncz", 5, 23 ),
                Arguments.of( "nppdvjthqldpwncqszvftbrmjlhg", 6, 23 ),
                Arguments.of( "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10, 29 ),
                Arguments.of( "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11, 26 )
        
        );
    }
    
    @ParameterizedTest
    @MethodSource( "testInputs" )
    void code( String input, int expected, int star2Expected )
    {
        assertEquals( expected, Dec6.code( input, StarLevel.STAR_1 ) );
        assertEquals( star2Expected, Dec6.code( input, StarLevel.STAR_2 ) );
    }
    
    @Test
    void actual()
    {
        String actualInput =
                "";
        assertEquals( 1804, Dec6.code( actualInput, StarLevel.STAR_1 ) );
        assertEquals( 2508, Dec6.code( actualInput, StarLevel.STAR_2 ) );
    }
}